sys_script_include.script:
var GetWhenNeeded = Class.create();
// Extend the global.AbstractAjaxProcessor class
GetWhenNeeded.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    // Define the getWhenNeeded function.  
    // Create a GlideRecord for the User table.
    // Use the sysparm_userID passed from the client side to retrieve a record from the User table.
    // Return the email address for the requested record
    getWhenNeeded: function() {
        var reqType = this.getParameter('sysparm_request_type');
        var whenNeeded = new GlideCalendarDateTime();
        var niutils = new NeedItUtils();
        var leadTime = niutils.getLeadTime(reqType,'suggested');
        whenNeeded.addSeconds(leadTime * 3600);

        return whenNeeded;
    },
    type: 'GetWhenNeeded'
});
